import React, { useState } from 'react';
import AddIcon from '@material-ui/icons/Add';

export const CreateNotes = (props) => {
  const [expand, setExpand] = useState(false);
  const [one, two] = useState({
    title: '',
    content: '',
  });

  const InputEvent = (e) => {
    // let [name, value] = e.target;

    let name = e.target.name;
    let value = e.target.value;

    console.log({ name });
    console.log({ value });

    two((preVal) => {
      return { ...preVal, [name]: value };
    });
  };

  const addItem = () => {
    props.passNote(one);
    two({
      title: '',
      content: '',
    });
  };
  const expand1 = () => {
    setExpand(!expand);
  };
  return (
    <>
      <div className="text-center">
        {expand ? (
          <input
            type="text"
            placeholder="title"
            onChange={InputEvent}
            name="title"
            value={one.title}
          />
        ) : null}
        <textarea
          placeholder="Write a Note..."
          onChange={InputEvent}
          name="content"
          value={one.content}
          onClick={expand1}
        ></textarea>
        {expand ? (
          <button onClick={addItem}>
            <AddIcon />
          </button>
        ) : null}
      </div>
    </>
  );
};
