import React, { useState } from 'react';
import ToDo from './ToDo';

const Apps = () => {
  const [item, setItem] = useState('');
  const [updateItem, setUpdateItem] = useState([]);

  const inputVal = (e) => {
    setItem(e.target.value);
  };

  const addItem = () => {
    setUpdateItem((preVal) => {
      return [...preVal, item];
    });
  };

  const deleteItems = () => {};
  return (
    <>
      <div className="outer-div">
        <div className="inner-div">
          <h1>ToDoList</h1>
          <input
            type="text"
            placeholder="Add a Items"
            onChange={inputVal}
            value={item}
          />
          <button onClick={addItem}>Add</button>

          <div>
            {updateItem.map((curItem, index) => {
              return <ToDo id={index} data={curItem} funct={deleteItems} />;
            })}
          </div>
        </div>
      </div>
    </>
  );
};

export default Apps;
