import React from 'react';
import { useState } from 'react';
import Table from './Table';
import { Users } from './users';

const App = () => {
  const [query, setQuery] = useState('');
  console.log(query);
  return (
    <>
      <div className="app">
        <input
          type="text"
          placeholder="Search..."
          className="search"
          onChange={(e) => setQuery(e.target.value)}
        />
        <Table />
      </div>
    </>
  );
};
export default App;

