import React from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import Home from './Home';
import About from './About';
import Contact from './Contact';
import Error from './Error';
import User from './User';
// React Router version 6:

const App = () => {
  return (
    <>
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Home />}>
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
            <Route path="/user:name" element={<User />} />
            <Route path="*" element={<Error />} />
          </Route>
        </Routes>
      </BrowserRouter>
    </>
  );
};

export default App;

// useParams Hooks in React Router:

// Agar ham kisi page me koi particular cheez ko he find karna chate hai tab hame useParam Hook ki jarurat padti hai.

// 'path="/user:name'  ye jo hamne is tarike se name lika hai isi ko ham kehte hai parameter, ham ek extra parameter add kar rahe hai jisse ham baad me usko dynamically fetch kara sakte hai. Ise ye hoga ki agar koi user urm me user ke baad forward slice and then kuch likta hai to bho hame dik jaana chaiye.
