import React, { useState } from 'react';

// React Hooks Challenge: Get Time on Refreshing and Clicking Button using useState Hook in Hindi

const App = () => {
  const newTime = new Date().toLocaleTimeString();

  const [ctime, setCtime] = useState(newTime);

  const updateTime = () => {
    newTime = new Date().toLocaleTimeString(); // hamne yaha par date method isliye pass ki hai jisse hame latest time mil jaaye, kyuki newTime varible ke ander jo time aaya tha bho to ek baar he yaha aajega lekin update nahi hoga click karne par kyuki bho function ke outside hai isliye hamne phir se date method se time nikala hai is function ke anderisse current time mil jayega.
    setCtime(newTime);
  };

  return (
    <>
      <h1>{ctime}</h1>
      <button onClick={updateTime}>Get Time</button>
    </>
  );
};

export default App;

// So now kitni easily in ReactJS ham value ko change kara sakte hai with the help of Reac Hooks.
