import React, { useState } from 'react';

// React Login Form Challenge in React

const App = () => {
  const [fullName, setFullName] = useState({
    fname: '',
    lname: '',
    email: '',
    phone: '',
  });

  const inputName = (event) => {
    console.log(event.target.value);
    console.log(event.target.name);

    // const value = event.target.value;
    // const name = event.target.name;

    const { value, name } = event.target; // using Object Distructing
    setFullName((preValue) => {
      if (name === 'fName') {
        return {
          fname: value,
          lname: preValue.lname,
          email: preValue.email,
          phone: preValue.phone,
        };
      } else if (name === 'lName') {
        return {
          fname: preValue.fname,
          lname: value,
          email: preValue.email,
          phone: preValue.phone,
        };
      } else if (name === 'email') {
        return {
          fname: preValue.fname,
          lname: preValue.lname,
          email: value,
          phone: preValue.phone,
        };
      } else if (name === 'phone') {
        return {
          fname: preValue.fname,
          lname: preValue.lname,
          email: preValue.email,
          phone: value,
        };
      }
    });
  };
  const onSubmits = (event) => {
    event.preventDefault();
    alert('Form Submitted');
  };

  return (
    <>
      <div>
        <form onSubmit={onSubmits}>
          <div>
            <h1>
              Hello {fullName.fname} {fullName.lname}
            </h1>
            <p>{fullName.email}</p>
            <p>{fullName.phone}</p>
            <input
              type="text"
              placeholder="Enter Your First Name"
              name="fName"
              onChange={inputName}
              value={fullName.fname}
            ></input>
            <br />
            <br />
            <input
              type="text"
              placeholder="Enter Your Last Name"
              name="lName"
              onChange={inputName}
              value={fullName.lname}
            ></input>
            <br />
            <br />
            <input
              type="emai/"
              placeholder="Enter Your Last Name"
              name="email"
              onChange={inputName}
              value={fullName.email}
            ></input>
            <br />
            <br />
            <input
              type="number"
              placeholder="Enter Your Last Name"
              name="phone"
              onChange={inputName}
              value={fullName.phone}
            ></input>
            <br />
            <br />
            <button type="submit">Click Me</button>
          </div>
        </form>
      </div>
    </>
  );
};

export default App;

//  Ham yaha par sabhi input fields ke data ko store kar rahe hai jo user ne type kiya hoga, ab agar koi submit par click karega to is pure data ko kahi or pass out kar sakte ho.
