import React, { useState } from 'react';
import List from './List';

// Create Accordion using React:

const App = () => {
  const [one, two] = useState();
  const [three, four] = useState([]);

  const inputEvent = (e) => {
    two(e.target.value);
  };
  const addItem = () => {
    four((preValue) => {
      return [...preValue, one];
    });

    two(' ');
  };

  const deleteItem = (id) => {
    four((preValue) => {
      return preValue.filter((index) => {
        return index !== id;
      });
    });
  };
  return (
    <>
      <div className="main_div">
        <div className="center_div">
          <h1>React ToDo List</h1>
          <input
            type="text"
            placeholder="Add the item"
            onChange={inputEvent}
            value={one}
          />
          <button onClick={addItem}>Click Me</button>

          <ul>
            {three.map((val, index) => {
              return (
                <List key={index} id={index} text={val} onSelect={deleteItem} />
              );
            })}
          </ul>
          <li>{val}</li>
        </div>
      </div>
    </>
  );
};

export default App;
