import React, { useState } from 'react';

const App = () => {
  const [newName, updateName] = useState(name);

  const inputName = (event) => {
    //  jab ye function triger ho raha hota hai to ye object bhi pass karte hai isme jo event perform hua hai uski sabhi information hoti hai.
    // "event.target.value" isse ham, user jo bhi type karega input field me usko  print karba sakege.
    // updateName(event.target.value);
  };

  const clickme = (event) => {
    updateName('the');
  };

  return (
    <>
      <div>
        <h1>Hello {newName}</h1>
        <input
          type="text"
          placeholder="Enter Your Name"
          onChange={inputName}
          value={newName}
        ></input>
        <button onClick={clickme}>Click Me</button>
      </div>
    </>
  );
};

export default App;

// value={newName} ye isliye lika hai kyuki ham user jo bhi type kar raha hai usko ham is name waale variable me store kara rahe hai
