import React, { useState } from 'react';

const App = () => {
  const [newName, updateName] = useState();

  const [fullName, setFullName] = useState();

  const inputName = (event) => {
    //  jab ye function triger ho raha hota hai to ye object bhi pass karte hai isme jo event perform hua hai uski sabhi information hoti hai.

    updateName(event.target.value); // "event.target.value" isse ham, user jo bhi type karega input field me usko  print karba sakege. Ab jaise he input filed me user jo bhi text type karenge bho newName ke liye current value ho jaayegi.
  };

  const onSubmit = () => {
    setFullName(newName);
  };

  return (
    <>
      <div>
        <h1>Hello {fullName}</h1>
        <input
          type="text"
          placeholder="Enter Your Name"
          onChange={inputName}
        ></input>
        <button onClick={onSubmit}>Click Me</button>
      </div>
    </>
  );
};

export default App;

// value={newName} ye isliye lika hai kyuki ham user jo bhi type kar raha hai usko ham is name waale variable me store kara rahe hai ham sabhi jagah ek he variable 'newName' ka use kar rahe hai and that is "Single source of truth". Matlab hamare liye input field me ye {name} he hai Single source of actual truth ki user ye type kar raha hai. Isse hame ab pata chal jaayega ki  user jo bhi type kar raha hai bho newName variable me store ho raha hai.

// Flow of above code:

// 'event.target.value' se ham user jo bhi type kar rahe hai input field me usko type kar rehe hai, ab ye 'event.target.value' ko hamne updateName() me pass kiya hai jisse newName variable ke pass jo use type karega bho text store ho jaayega newName variable ki cueent value bankar, uske baad ham is newName variable ko pass kar rahe hai input filed ke ander value property me jisse hame pata chal jaayega ki user kya text type kar raha hai.
