import React, { useState } from 'react';

// useEffect Hook in React:

const App = () => {
  const [num, setNum] = useState(0);
  return (
    <button
      onClick={() => {
        setNum(num + 1);
      }}
    >
      Click Me {num}
    </button>
  );
};

export default App;
