import React, { useState } from 'react';

// How to Handling Complex Multiple Input Form States in React:

const App = () => {
  const [fullName, setFullName] = useState({
    fname: '',
    lname: '',
  }); // Hamne useState ke nader two object pass kar diya hai.

  const inputName = (event) => {
    console.log(event.target.value); // isse ham jo type karenge bho hame mil jaayega

    console.log(event.target.name); // isse hame name attribute ki value mil jaayegi.

    console.log(event.target.name); // isse hame placeholder ki value mil jaayegi.

    const value = event.target.value; // isse ham jo type karenge bho hame mil jaayega or usko hamne value variable me store kar liya hai.

    const name = event.target.name; // isse hame name attribute ki value mil jaayegi or usko hamne name variable me store kar liya hai.
  };

  setFullName((preValue) => {
    // setFullName function ke anderhamne ek callback function pass kiya hai, ham setFUllName function se ham element ki privious state (previous value) kya thi iska bhi pata laga sakte hai. Hanmne callback function me ek parameter psss kiya hai aap kuch bhi name de sakte ho. Jo object fullName ke pass hai usi object ko setFullName bhi access kar sakta hai. Yaha hamne setFullName ki help se callback function me preValue parameter pass kar diya hai, ab is parameter me fullName wala access a jaayega.
    // console.log(preValue); // fullName ke pass jo object hoga usi ko print kar dega. Isi tarah preValue.fname se muje fname ki value mil jaayegi and preValue.lname se muje fname ki value mil jaayegi.
  });

  const onSubmits = (event) => {
    event.preventDefault();
    alert('Form Submitted');
  };

  return (
    <>
      <div>
        <form onSubmit={onSubmits}>
          <div>
            <h1>
              Hello {fullName.fname} {fullName.lname}
            </h1>
            <input
              type="text"
              placeholder="Enter Your First Name"
              name="fName"
              onChange={inputName}
              value={fullName.fname}
            ></input>
            <br />
            <br />
            <input
              type="text"
              placeholder="Enter Your Last Name"
              name="lName"
              onChange={inputName}
              value={fullName.lname}
            ></input>
            <br />
            <br />
            <button type="submit">Click Me</button>
          </div>
        </form>
      </div>
    </>
  );
};

export default App;

// Ham JavaScript me kaam kare ya php me, jab bhihame form me backend me data bhejna padta tha ki input me kya likaa kya hai to ham ek attribute use karte the input field me taht is "name" attribute jo bahut he jyada important hai. Isliye hamne yaha name attribute ka use kiya hai.

// useState() me ham object bhi pass kar sakte hai, Yeh useState() bhi Object return kar sakta hai.

// Value attribute me hane fullName.fname and fullName.lname islye kiya hai kyuki jo object hamne setStateme() me pass kiya bho object fullName ke liye current data ho gaya matlab fullname me ab ek object aa gaya hai or object ki property ko access karne ke liye ham dot notation ka use karte hai. Jisse hamne fname and lname me jo bhi data ho usko ham Value attribute me pass kar sake.
