import React from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import Home from './Home';
import About from './About';
import Contact from './Contact';
import Error from './Error';
// React Router version 6:

const App = () => {
  return (
    <>
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Home />}>
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
            <Route path="*" element={<Error />} />
          </Route>
        </Routes>
      </BrowserRouter>
    </>
  );
};

export default App;

// Nested Route:

// Nested Route banane ke liye jo hamara main page hai (path="/") ye wala, iske self closing tag ko ham hata denge or iska closing tag end me aakar rak denge, ab ye main Route hamara nested Route ban chuka hoga kyuki iske ander bhi kai saare Route ho sakte hai.

// Example: Nested Routing

<BrowserRouter>
  <Routes>
    <Route path="/" element={<Home />}>
      <Route path="/about" element={<About />} />
      <Route path="/contact" element={<Contact />} />
      <Route path="*" element={<Error />} />
    </Route>
    // ye main route ka closing tah hai, ab ye main Route hamara nested Route
    ban chuka hoga kyuki iske ander bhi kai saare Route ho sakte hai.
  </Routes>
</BrowserRouter>;
