import React, { useState } from 'react';

const App = () => {
  const [inputList, setInputList] = useState('');
  const [Items, setItems] = useState([]); // iska matlan hai Items ke ander jo current data hai bho hai ek empty array, matlab Items ab ek array ban chuka hai.

  const itemEvent = (e) => {
    setInputList(e.target.value);
  };

  const listofItems = () => {};
  // Ab hame ek array chaiye jo apni state (value) ko change kar raha hai isleye hamne second useState Hook ka use kiya hai.
  return (
    <>
      <div className="main_div">
        <div className="center_div">
          <br />
          <h1>ToDo List</h1>
          <br />
          <input type="text" placeholder="Add a Items" onChange={itemEvent} />
          <button onClick={listofItems}> + </button>

          <ol>
            <li> {inputList} </li>
          </ol>
        </div>
      </div>
    </>
  );
};

export default App;
