import React, { useState } from 'react';

const CreateNotes = (props) => {
  const [input, setInput] = useState({
    title: '',
    content: '',
  });

  const InputValue = (e) => {
    const name = e.target.name;
    const value = e.target.value;

    setInput((preVal) => {
      return {
        ...preVal,
        [name]: value,
      };
    });
  };

  const addItems = () => {
    props.newAddItems(input);
  };
  return (
    <>
      <div>
        <input
          type="text"
          placeholder="Title"
          name="title"
          onChange={InputValue}
        />
        <textarea
          row=""
          column=""
          type="text"
          placeholder="Write a Note"
          name="content"
          onChange={InputValue}
        />

        <button onClick={addItems}>Add</button>
      </div>
    </>
  );
};

export default CreateNotes;
