import React, { useEffect, useState } from 'react';

// useEffect Hook in React:

const App = () => {
  const [num, setNum] = useState(0);
  const [nums, setNums] = useState(0);

  useEffect(() => {
    // jab bhi index.js file me render method call hoga to ye useEffect hook call hoga kyuki hamne is <App /> component ko render method ke ander call kiya hai.
    alert('I am clicked');
  }, [num]); // empty array dene se useEffect hook tabhi chalega jab aapka page first time refferce hoga, uske baad kabhi nahi chalega. Ab hamne array me num pass kiya hai isliye ab jab num increment hoga tabhi useEffect call hoga.
  return (
    <>
      <button
        onClick={() => {
          setNum(num + 1);
        }}
      >
        Click Me {num}
      </button>
      <br />
      <br />
      <button
        onClick={() => {
          setNums(nums + 1);
        }}
      >
        Click Me {nums}
      </button>
    </>
  );
};

export default App;
