import React, { useState } from 'react';

const App = () => {
  const purple = 'red';
  const [bg, setBg] = useState(purple);

  const [name, updname] = useState('Click Me');

  const bgchange = () => {
    let newBg = 'yellow';
    setBg(newBg);

    updname('OUCH!!');
  };

  const bgBack = () => {
    setBg(purple);
  };

  return (
    <>
      <div style={{ backgroundColor: bg }}>
        <button onClick={bgchange} onDoubleClick={bgBack}>
          {name}
        </button>
      </div>
    </>
  );
};

export default App;

// Ab hame backgroundColor ko red se purple karna hai matlab hame backgroudColor ki value change karni hai matlab state change karni hai isliye ab ham isme Hooks yaani ki useState() ka use karenge, to isi tarah state change (value change) karne ke liye ham Hooks ka use karne hai.
