import React, { useEffect, useState } from 'react';

// useEffect hook CleanUp Function:

const UseEffects4 = () => {
  const [widthCount, setWidthCount] = useState(window.screen.width);

  const actualWidth = () => {
    setWidthCount(window.innerWidth);
  };

  useEffect(() => {
    window.addEventListener('resize', actualWidth); // jab jab 'resize' event fire hoga hamara 'actualWidth' function call ho jaayega.
  });
  return (
    <>
      <h2>The actual size of window is:</h2>
      <h1>{widthCount}</h1>
    </>
  );
};

export default UseEffects4;
