import React, { useState } from 'react';

const App = () => {
  const [newName, updateName] = useState();

  const [fullName, setFullName] = useState();

  const inputName = (event) => {
    //  jab ye function triger ho raha hota hai to ye object bhi pass karte hai isme jo event perform hua hai uski sabhi information hoti hai.
    // "event.target.value" isse ham, user jo bhi type karega input field me usko  print karba sakege.
    // updateName(event.target.value);
  };

  const onSubmit = (event) => {
    const upd = newName;
    setFullName(upd);
  };

  return (
    <>
      <div>
        <h1>Hello {fullName}</h1>
        <input
          type="text"
          placeholder="Enter Your Name"
          onChange={inputName}
          value={newName}
        ></input>
        <button onClick={onSubmit}>Click Me</button>
      </div>
    </>
  );
};

export default App;

// value={newName} ye isliye lika hai kyuki ham user jo bhi type kar raha hai usko ham is name waale variable me store kara rahe hai ham sabhi jagah ek he variable 'newName' ka use kar rahe hai and that is "Single source of truth". Matlab hamare liye input field me ye {name} he hai Single source of actual truth ki user ye type kar raha hai. Isse hame ab pata chal jaayega ki  user jo bhi type kar raha hai bho newName variable me store ho raha hai.
