import React, { useState } from 'react';
import { CreateNotes } from './CreateNotes';

const Apps = () => {
  const [three, four] = useState([]);
  const addNote = (one) => {
    four((oldItem) => {
      return [...oldItem, one];
    });
  };
  return (
    <>
      <CreateNotes passNote={addNote} />
      {three.map((val, index) => {
        return <Notes key={index} id={index} name={val} />;
      })}
    </>
  );
};

export default Apps;
