import React, { useState } from 'react';

const App = () => {
  const [newName, updateName] = useState();

  const [fullName, setFullName] = useState();

  const inputName = (event) => {
    updateName(event.target.value);
  };

  const onSubmits = (event) => {
    event.preventDefault();
    setFullName(newName);
  };

  return (
    <>
      <div>
        <form onSubmit={onSubmits}>
          <div>
            <h1>Hello {fullName}</h1>
            <input
              type="text"
              placeholder="Enter Your Name"
              onChange={inputName}
              value={newName}
            ></input>

            <button type="submit">Click Me</button>
          </div>
        </form>
      </div>
    </>
  );
};

export default App;

// Jab bhi ham Input field par kaam kar rahe hai to iska matlab hai ki ham kaam kar rahe hai Controlled Component par, its means hamare jo input elemnet hai usko yeh <form> Controlled nahi kar raha balki hamara React component Input field controlled kar raha hai.
