import { Category } from '@material-ui/icons';
import React, { useState } from 'react';
import Menu from './Menu';

const GalleryReact = () => {
  const [items, setItems] = useState(Menu);

  const filterItem = (cateItem) => {
    const updatedItems = Menu.filter((curElem) => {
      return curElem.category === cateItem;
    });
    setItems(updatedItems);
  };

  return (
    <>
      <h1 className="mt-5 text-center main-heading">
        Order Your Favorite Dish
      </h1>
      <hr />
      <div className="menu-tabs container">
        <div className="menu-tab d-flex justify-content-around">
          <button
            className="btn btn-warning"
            onClick={() => filterItem('breakfast')}
          >
            Breakfast
          </button>
          <button className="btn btn-warning">Lunch</button>
          <button className="btn btn-warning">Evening</button>
          <button className="btn btn-warning">Dinner</button>
          <button className="btn btn-warning">All</button>
        </div>
      </div>

      <div className="menu-items container-fluid mt-5">
        <div className="row">
          <div className="col-11 mx-auto">
            <div className="row my-5">
              {items.map((elem) => {
                const { id, name, image, description, price } = elem;
                return (
                  <div className="item1 col-12 col-md-6 col-lg-6 col-xl-4 my-5">
                    <div className="row item-inside">
                      <div className="col-12 col-md-12 col-lg-4 img-div">
                        <img src={image} alt="menu-pic" className="img-fluid" />
                      </div>

                      <div className="col-12 col-md-12 col-lg-8">
                        <div className="main-title pt-4pb-3">
                          <h1>{name}</h1>
                          <p>{description}</p>
                        </div>
                        <div className="menu-price">
                          <div className="price-divide d-flex justify-content-between align-items-center">
                            <h2>{price}</h2>
                            <a href="">
                              <button className="btn btn-primary">
                                Order Now
                              </button>
                            </a>
                          </div>
                          <p>*Prices may vary on selected date.</p>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default GalleryReact;
