import React, { useEffect, useState } from 'react';
import axios from 'axios';

// React API Call to Get Pokemon JSON Data using Axios and useEffect in ReactJS:

const App = () => {
  const [num, setNum] = useState();
  const [name, setName] = useState();
  const [moves, setMoves] = useState();
  useEffect(() => {
    async function getData() {
      const res = await axios.get(`https://pokeapi.co/api/v2/pokemon/${num}`);
      setName(res.data.name);
      setMoves(res.data.name);
      console.log(res.data.moves.length);
    }
    getData();
  });
  return (
    <>
      <h1>
        You Choose <span style={{ color: 'red' }}>{num}</span> Value
      </h1>
      <h1>
        My name is <span style={{ color: 'red' }}>{name}</span>
      </h1>
      <h1>
        I have <span style={{ color: 'red' }}>{moves}</span> moves
      </h1>
      <select
        value={num}
        onChange={(e) => {
          setNum(e.target.value);
        }}
      >
        <option value="1">1</option>
        <option value="24">24</option>
        <option value="3">3</option>
        <option value="4">4</option>
        <option value="5">5</option>
      </select>
    </>
  );
};

export default App;
