import React, { useState } from 'react';
import ToDoList from './ToDoList';

const App = () => {
  const [inputList, setInputList] = useState('');
  const [Items, setItems] = useState([]);
  // Ab hame ek array chaiye jo apni state (value) ko change kar raha hai isleye hamne second useState Hook ka use kiya hai.
  // iska matlab hai Items ke ander jo current data hai bho hai ek empty array, matlab Items ab ek array ban chuka hai.

  const itemEvent = (e) => {
    setInputList(e.target.value);
  };

  const listofItems = () => {
    setItems((olditems) => {
      // useState([]) iske pass jo bhi data hai isko setItems access kar sakta hai is tarike se callback function me parameter pass karke, ab ye oldItems parameter, useState ke array ke pass jo bhi data hai usko access kar sakta hai.
      return [...olditems, inputList]; // ...olditems me jo bhi data hai usko hamne new array me store kar liya or inputList ko bhi pass kar diya kyuki inputList ke pass current data hai. basically hamne setItems ko he array bana diya ab is setItems ka ye array wala data Items ke liye current data ho jaayega, or is Items par hamne map method call kara diya hai jisse ye data <li> list me add ho jaaye.
      // setItmes or oldItems me purana wala data store hota rahega or jaise he inputList se new data aayega bho bhi isi setItems me store hoga or yahi Items ka current data hoga or jab koi  button par click karega to ham ye data map method ke thru <li> list me dika paayenge. Jaise jaise Items me new data add hota rahega wese wese map method ke thru ye data <li> me show ho jaayega. Basically ...oldItems  array me jo empty data tha bho jaise jaise inputList se new data aata rahega bho ...oldItems array me store hota rahega or yahi array  Items ke liye new array data hoga jispar hamne map method lagaya hai. Basically ...oldItems array new data aane ke baad empty nahi rahega.
    });

    setInputList(''); // isko empty karne se inputList bhi empty ho jaayega button par click karne ke baad, or button par click karne se input field se purana wala text automatcally remove ho jaayega jisse ham new text type kar paayenge.
  };

  const deleteItem = (id) => {
    // deleteItem ko call karte samaye hamne jo id pass ki hai bho yaha a jaayegi, is id ke se he ham, user ne jis item ko delete karne ke liye click kiya hai usko identify kar paayenge.
    console.log('delete');

    setItems((olditems) => {
      // olditems array se hame element delete karna hai.
      return olditems.filter((arrElem, index) => {
        return index !== id; // olditems array ke jis element ki id se ye id match kar jaayegi uske chodkar baaki ke element ko return kar denge new array me, or jo element match karega bho delete ho jaayega. And ab ye new array setItems ke liye updated array hoga iska matlab hai ye new array Items ke liye current array hoga.
      });
    });
  };

  return (
    <>
      <div className="main_div">
        <div className="center_div">
          <br />
          <h1>ToDo Lists</h1>
          <br />
          <input
            type="text"
            placeholder="Add a Items"
            onChange={itemEvent}
            value={inputList}
          />
          <button onClick={listofItems}> + </button>

          <ol>
            {Items.map((itemval, index) => {
              return (
                <ToDoList
                  key={index}
                  id={index}
                  text={itemval}
                  onSelect={deleteItem}
                />
              );
            })}
          </ol>
        </div>
      </div>
    </>
  );
};

export default App;

// <toDoList> component me hamne ek id props banaya hai kyuki har ek items ki ek unique id honi chaiye jisse ham usko identify kar sake jisse ham us id ke based par kisi bhi item ko array se delete kar sake jab requiremnet ho.

// and onSelect props me hamne deleteItem function pass kiya hai.
