import React, { useState } from 'react';
import { CreateNotes } from './CreateNotes';
import Notes from './Notes';

const Apps = () => {
  const [three, four] = useState([]);
  const addNote = (one) => {
    four((oldItem) => {
      return [...oldItem, one];
    });
  };

  const itemDelete = (id) => {
    four((oldItem) => {
      return oldItem.filter((c, index) => {
        return index !== id;
      });
    });
  };
  return (
    <>
      <CreateNotes passNote={addNote} />
      {three.map((val, index) => {
        return (
          <Notes
            key={index}
            id={index}
            title={val.title}
            content={val.content}
            deleteIcon={itemDelete}
          />
        );
      })}
    </>
  );
};

export default Apps;
