import React, { useState } from 'react';

// How to Handling Complex Multiple Input Form States in React:

const App = () => {
  const [firstName, updateName] = useState({
    firstName: '',
    lastName: '',
  });

  const inputName = (event) => {
    updateName(event.target.value);
  };

  const onSubmits = (event) => {
    event.preventDefault();
    alert('Form Submitted');
  };

  return (
    <>
      <div>
        <form onSubmit={onSubmits}>
          <div>
            <h1>Hello</h1>
            <input
              type="text"
              placeholder="Enter Your First Name"
              name="fName"
              onChange={inputName}
              value={firstName}
            ></input>
            <br />
            <br />
            <input
              type="text"
              placeholder="Enter Your Last Name"
              name="lName"
              onChange={inputName}
              value={lastName}
            ></input>
            <br />
            <br />
            <button type="submit">Click Me</button>
          </div>
        </form>
      </div>
    </>
  );
};

export default App;

// Ham JavaScript me kaam kare ya php me, jab bhihame form me backend me data bhejna padta tha ki input me kya likaa kya hai to ham ek attribute use karte the input field me taht is "name" attribute jo bahut he jyada important hai. Isliye hamne yaha name attribute ka use kiya hai.

