import React, { useState } from 'react';
import { question } from './api'; // hamne question ko api.js file me  export karte time default nahi lika isliye hamne import karte time usko curly bracket me add kiya hai.
import Accordion from './Accordion';
const MyAccordian = () => {
  const [data, setData] = useState(question);
  return (
    <>
      {data.map((curElem) => {
        const { id } = curElem; // object distruction kiya hai because curElem me array me se object aate rahenge one by one.
        return <Accordion id={id} {...curElem} />; // is ...curElem me remaining object ki properties hogi id ke alawa
      })}
    </>
  );
};

export default MyAccordian;
