import React, { useState } from 'react';
import AddIcon from '@material-ui/icons/Add';

export const CreateNotes = (props) => {
  const [one, two] = useState({
    title: '',
    content: '',
  });

  const InputEvent = (e) => {
    // let [name, value] = e.target;

    let name = e.target.name;
    let value = e.target.value;

    console.log({ name });
    console.log({ value });

    two((preVal) => {
      return { ...preVal, [name]: value };
    });
  };

  const addItem = () => {
    props.passNote(one);
  };
  return (
    <>
      <div className="text-center">
        <input
          type="text"
          placeholder="title"
          onChange={InputEvent}
          name="title"
          value={one.title}
        />
        <textarea
          placeholder="Write a Note..."
          onChange={InputEvent}
          name="content"
          value={one.content}
        ></textarea>
        <button onClick={addItem}>
          <AddIcon />
        </button>
      </div>
    </>
  );
};
