import { createGlobalStyle } from 'styled-components';

const GlobalStyle = createGlobalStyle`

body {
  height: 100vh;
  display: grid;
  place-items: center;
  background-color: ${({ theme }) => theme.color.bg};
}
`;

export default GlobalStyle;

// Global style ka benefit ye hoga ki aap isme JS ka code bhi use kar sakte ho, jo ki ham noram css file me nahi kar sakte.

// Ab ham is file ko export karenge jaha hame ye css ka code use karna hai
