import React from 'react';
import styled from 'styled-components';
import { ThemeProvider } from 'styled-components';
import GlobalStyle from './GlobalStyle';

// Global Styles in Styled Component:

// Global CSS is like your external CSS.

const App = () => {
  const theme = {
    // theme provider
    color: {
      textColor: 'red',
      bg: 'yellow',
    },
  };

  let myFavColor = 'red';

  const Button = styled.button`
    background-color: ${(props) => props.bg};
    color: ${myFavColor};
    font-size: 16px;
    text-align: center;
    padding: 6px 30px;

    &:hover {
      background-color: ${({ theme }) => theme.color.bg};
      color: #000;
    }
    &:active {
      background-color: red;
      color: #fff;
    }

    &:after {
      content: 'After Content';
    }

    span {
      color: blue;
      b {
        color: #fff;
      }
      .test {
        color: red;
      }
      &:hover {
        background-color: black;
        color: #fff;
      }
    }
  `;

  const Para = styled.p`
    color: red;
    font-size: 16px;

    &:hover {
      background-color: yellow;
      color: #000;
    }
  `;
  return (
    <ThemeProvider theme={theme}>
      <>
        <Button class="btn" bg="purple">
          Subcribe Shyam
          <span>
            Like and Share <b>Comment</b> <span class="test">Kardo Yaar</span>
          </span>
        </Button>
        <Para>Shyam Tahput</Para>
      </>
    </ThemeProvider>
  );
};

export default App;

