import React, { useEffect, useState } from 'react';

// useEffect Hook Challenge:

const App = () => {
  const [num, setNum] = useState(0);

  useEffect(() => {
    document.title = `You clicked me ${num}`;
  }, [num]);
  return (
    <>
      <button
        onClick={() => {
          setNum(num + 1);
        }}
      >
        Click Me {num}
      </button>
    </>
  );
};

export default App;
