import React, { useState } from 'react';
import CreateNotes from './CreateNotes';
import Notes from './Notes';

const Apps = () => {
  const [newdata, setNewData] = useState([]);

  const newAddItems = (input) => {
    setNewData((oldData) => {
      return [...oldData, input];
    });
  };

  const deleteItems = (id) => {
    setNewData((oldData) => {
      return oldData.filter((curItem, index) => {
        return index !== id;
      });
    });
  };
  return (
    <>
      <div>
        <CreateNotes funct={newAddItems} />
        {newdata.map((curVal, index) => {
          return (
            <Notes
              key={index}
              head={curVal.title}
              cont={curVal.content}
              functs={deleteItems}
            />
          );
        })}
      </div>
    </>
  );
};

export default Apps;
