import { useState } from 'react';

function App() {
  let colr = 'yellow';
  // console.log(times);
  const [one, two] = useState(yellow);

  function clickme() {
    times = new Date().toLocaleTimeString();
    two(times);
  }

  return (
    <>
      <div style={{ textAlign: one }}>
        <button>Click Me</button>
      </div>
    </>
  );
}

export default App;
