const App = () => {
  const [one, two] = useState();
  const [three, four] = useState();

  const [one1, two1] = useState();
  const [three1, four1] = useState();

  const inputEvent = (e) => {
    console.log(e.target.value);
    two(e.target.value);
  };

  const inputEvent2 = (e) => {
    console.log(e.target.value);
    two1(e.target.value);
  };

  const onSubmit = () => {
    four(one);
    four1(one1);
  };
  return (
    <>
      <div className="div">
        <h1>Hello, {three}</h1>
      </div>
      <form onSubmit={onSubmits}>
        <input
          type="text"
          placeholder="Enter your Name"
          onChange={inputEvent}
          value={three}
        ></input>
        <br></br>
        <br></br>
        <input
          type="text"
          placeholder="Enter your Name"
          onChange={inputEvent2}
          value={three}
        ></input>
        <br></br>
        <br></br>
        <button type="submit">Click Me</button>
      </form>
    </>
  );
};

export default App;
