import React from 'react';
import styled from 'styled-components';

// How to pass PROPS in Styled Components:

const App = () => {
  let myFavColor = 'red';
  const Button = styled.button`
    background-color: ${(props) =>
      props.bg}; /* yaha hamne props ko get kiya hai, we need to use arrow function for get props. */
    /* background-color: ${({ bg }) =>
      bg}; // yaha hamne distructing ka use kiya hai ye bhi sahi hai */
    color: ${myFavColor};
    font-size: 16px;
    text-align: center;
    padding: 6px 30px;

    &:hover {
      background-color: yellow;
      color: #000;
    }
    &:active {
      background-color: red;
      color: #fff;
    }

    &:after {
      content: 'After Content';
    }

    span {
      color: blue;
      b {
        color: #fff;
      }
      .test {
        color: red;
      }
      &:hover {
        background-color: black;
        color: #fff;
      }
    }
  `;

  const Para = styled.p`
    color: red;
    font-size: 16px;

    &:hover {
      background-color: yellow;
      color: #000;
    }
  `;
  return (
    <>
      <Button class="btn" bg="purple">
        Subcribe Shyam
        <span>
          Like and Share <b>Comment</b> <span class="test">Kardo Yaar</span>
        </span>
      </Button>
      <Para>Shyam Tahput</Para>
    </>
  );
};

export default App;

