import React from 'react';
import { useState } from 'react';

function App() {
  let colr = 'yellow';
  // console.log(times);
  const [one, two] = useState(colr);

  const [three, four] = useState('Click Me');

  function clickme() {
    two('red');
  }

  function dblc() {
    two('yellow');
  }

  return (
    <>
      <div style={{ textAlign: 'center', backgroundColor: one }}>
        <button onClick={clickme} onDoubleClick={dblc}>
          {three}
        </button>
      </div>
    </>
  );
}

export default App;
