import React from 'react';
import styled, { ThemeProvider } from 'styled-components';

// Theme Provider in Styled Component:

// Theme Provider is like a wharehouse. Agar hame pata hai ki koi common props hai jo bahut jagah use hone wala hai to ham is props ko is Theme Provider ke ander lik sakte hai taaki aap baad me usko easily change kar sake.

const App = () => {
  const theme = {
    // theme provider
    color: {
      textColor: 'red',
      bgs: 'yellow',
    },
  };

  let myFavColor = 'red';

  const Button = styled.button`
    background-color: ${(props) => props.bg};
    color: ${myFavColor};
    font-size: 16px;
    text-align: center;
    padding: 6px 30px;

    &:hover {
      background-color: ${({ theme }) => theme.color.bgs};
      color: #000;
    }
    &:active {
      background-color: red;
      color: #fff;
    }

    &:after {
      content: 'After Content';
    }

    span {
      color: blue;
      b {
        color: #fff;
      }
      .test {
        color: red;
      }
      &:hover {
        background-color: black;
        color: #fff;
      }
    }
  `;

  const Para = styled.p`
    color: red;
    font-size: 16px;

    &:hover {
      background-color: yellow;
      color: #000;
    }
  `;
  return (
    <>
      <Button class="btn" bg="purple">
        Subcribe Shyam
        <span>
          Like and Share <b>Comment</b> <span class="test">Kardo Yaar</span>
        </span>
      </Button>
      <Para>Shyam Tahput</Para>
    </>
  );
};

export default App;

// here bg="green" is a props, its like attribute
