import React, { useEffect, useState } from 'react';

// useEffect Hook in React:

const App = () => {
  const [num, setNum] = useState(0);

  useEffect(() => {
    // jab bhi index.js file me render method call hoga to ye useEffect hook call hoga kyuki hamne is <App /> component ko render method ke ander call kiya hai.
    alert('I am clicked');
  }, []); // empty array dene se useEffect hook tabhi chalega jab aapka page first time refferce hoga.
  return (
    <button
      onClick={() => {
        setNum(num + 1);
      }}
    >
      Click Me {num}
    </button>
  );
};

export default App;
