import React, { useState } from 'react';

const CreateNote = () => {
  const [note, setNote] = useState({
    title: '',
    content: '',
  });

  const InputEvent = (e) => {
    const value = e.target.value;
    const name = e.target.name;

    setNote((prevData) => {
      return {
        ...prevData,
      };
    });
  };

  return (
    <>
      <div className="main_note">
        <form>
          <input
            type="text"
            name="title"
            placeholder="Title"
            autoComplete="off"
            onChange={InputEvent}
            value={note.title}
          />
          <textarea
            rows=""
            column=""
            name="content"
            placeholder="Write a note"
            onChange={InputEvent}
            value={note.content}
          />
          <button>+</button>
        </form>
      </div>
    </>
  );
};

export default CreateNote;
