import React from 'react';
import { Link } from 'react-router-dom';
const Menu = () => {
  return (
    <>
      <Link to="/"> AboutUS </Link>
      <Link to="/contact"> ContactUS </Link>
      <br />
      <a href="/">AboutUS</a>
      <a href="/contact">ContactUS</a>
    </>
  );
};

export default Menu;

// Instead of anchor tag, we use Link in the React Router and 'href' ki jagah ham 'to' ka use karte hai. Lekin ham Link me koi class add nayi kar paate isliye ham 'Link' ki jagah ab 'NavLink' ka use karte hai.

// Ham 'Link' ke wajaye 'NavLink' ka use kar sakte hai kyuki isme ek attribute hota hai 'activeClassName' iski help se class add kar sakte hai. Ex: <navLink activeClassName="active_class" to="/">
