import React, { useState } from 'react';

const UseStateObject = () => {
  const [myObject, setMyObject] = useState({
    myName: 'shyam',
    myAge: 27,
    degree: 'MSC',
  });

  const changeObject = () => {
    setMyObject({ ...myObject, myName: 'Shyam rajput', myAge: 26 }); // ...myObject se yaha par object ki properties clone hokar aa gayi ab isme 'myName' and myAge ki value change ho jaayegi and degree ki value same rahegi kyuki hamne degree ki value change nahi ki..
  };
  return (
    <>
      <h1>Name: </h1>
      <button onClick={changeObject}></button>
    </>
  );
};

export default UseStateObject;
