import React, { useState } from 'react';

const ToDoList = () => {
  const myBioData = [
    {
      id: 0,
      myName: 'shyam',
      age: 28,
    },
    {
      id: 1,
      myName: 'rajput',
      age: 29,
    },
  ];

  const [myArray, setmyArray] = useState(myBioData);

  const clearArray = () => {
    setmyArray([]);
  };

  const removeElem = (id) => {
    const myNewArray = myArray.filter((curElm) => {
      curElm.id === id;
    });
  };

  return (
    <>
      {myArray.map((curElm) => {
        return (
          <h1 className="h1style" key={curElm.id}>
            Name: {curElm.myName} & Age: {curElm.age}
            <button onClick={() => removeElem(curElm.id)}>remove</button>
          </h1>
        );
      })}

      <button onClick={clearArray} className="btn">
        Clear
      </button>
    </>
  );
};

export default ToDoList;
