import React, { useState } from 'react';
import Header from './Header';
import Footer from './Footer';
import CreateNote from './CreateNote';
import Note from './Note';

const App = () => {
  const [addItem, setAddItem] = useState([]);

  const addNote = (note) => {
    setAddItem((oldData) => {
      return [...oldData, note];
    });
    console.log(note);
  };
  return (
    <>
      <Header />
      <CreateNote passNote={addNote} />
      <Note />
      <Footer />
    </>
  );
};

export default App;
