import React, { useState } from 'react';

const App = () => {
  const inputName = (event) => {
    //  jab ye function triger ho raha hota hai to ye object bhi pass karte hai isme jo event perform hua hai uski sabhi information hoti hai.
    console.log(event.target.value); // isse ham, user jo bhi type karege input field me usko console me print karba sakoge.
  };

  const name = 'none';

  const [newName, updateName] = useState(name);

  return (
    <>
      <div>
        <h1>{name}</h1>
        <input
          type="text"
          placeholder="Enter Your Name"
          onChange={inputName}
        ></input>
        <button>Click Me</button>
      </div>
    </>
  );
};

export default App;
