import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const loginBtn = () => {
    localStorage.setItem('login', true); // jaise he login set hoga to yaha se bhi usko login page par redirect kar dega.
    navigate('/');
  };

  const navigate = useNavigate();
  useEffect(() => {
    let login = localStorage.getItem('login');
    if (login) {
      // agar user already login hai then usko home page par redirect kar dega.
      navigate('/');
    }
  });
  return (
    <>
      <h1>Login Page</h1>
      <input type="text" /> <br />
      <input type="text" />
      <br />
      <button onClick={loginBtn}>Login</button>
    </>
  );
};

export default Login;
