import React, { useState } from 'react';

// How to Handling Complex Multiple Input Form States in React:

const App = () => {
  const [fullName, setFullName] = useState({
    fname: '',
    lname: '',
  }); // Hamne useState ke nader two object pass kar diya hai.

  const inputName = (event) => {
    updateName(event.target.value);
  };

  const onSubmits = (event) => {
    event.preventDefault();
    alert('Form Submitted');
  };

  return (
    <>
      <div>
        <form onSubmit={onSubmits}>
          <div>
            <h1>Hello</h1>
            <input
              type="text"
              placeholder="Enter Your First Name"
              name="fName"
              onChange={inputName}
              value={fullName.fname}
            ></input>
            <br />
            <br />
            <input
              type="text"
              placeholder="Enter Your Last Name"
              name="lName"
              onChange={inputName}
              value={fullName.lname}
            ></input>
            <br />
            <br />
            <button type="submit">Click Me</button>
          </div>
        </form>
      </div>
    </>
  );
};

export default App;

// Ham JavaScript me kaam kare ya php me, jab bhihame form me backend me data bhejna padta tha ki input me kya likaa kya hai to ham ek attribute use karte the input field me taht is "name" attribute jo bahut he jyada important hai. Isliye hamne yaha name attribute ka use kiya hai.

// useState() me ham object bhi pass kar sakte hai, Yeh useState() bhi Object return kar sakta hai.

// Value attribute me hane fullName.fname and fullName.lname islye kiya hai kyuki jo object hamne setStateme() me pass kiya bho object fullName ke liye current data ho gaya matlab fullname me ab ek object aa gaya hai or object ki property ko access karne ke liye ham dot notation ka use karte hai. Jisse hamne fname and lname me jo bhi data ho usko ham Value attribute me pass kar sake.
