import React, { useState } from 'react';

// Create Accordion using React:

const App = () => {
  const [one, two] = useState();
  const [three, four] = useState();

  const inputEvent = (e) => {
    two(e.target.value);
  };
  const addItem = () => {
    four(one);
    two(' ');
  };
  return (
    <>
      <div className="main_div">
        <div className="center_div">
          <h1>React ToDo List</h1>
          <input
            type="text"
            placeholder="Add the item"
            onChange={inputEvent}
            value={one}
          />
          <button onClick={addItem}>Click Me</button>

          <ul>
            <li>{three}</li>
          </ul>
        </div>
      </div>
    </>
  );
};

export default App;
