import React, { useState } from 'react';

const App = () => {
  const state = useState(); // state ki 2 value ha i array me [undefined, function]
  // console.log(state);

  const [count, setCount] = useState(0); // Array Distructing ka use kiya hai, ham useState() ke nader koi bhi value pass kar sakte hai. useState ki array me jo 2 values hai undefined and function bhi count and setCount me a jaayegi.

  // Yaha initial value 0 hai jo count variable me chali jaayegi. Here count is a current value

  // Ab setCount ek function hai and isme aap jo bhi value pass kroge bho hogi updated value. And, ab ye jo 'setCount' function ki Updated Value hai bho 'count' ke liye ho jaayegi Current Vlaue.

  const IncCum = () => {
    // setCount(100); // ab ye 100, count ke liye current value ho jaayegi.
    setCount(count + 5); // ab jitni baar aap button par click karenge utni baar ye +5 se increament ho jaayegi. Ab jaise - jaise ye value updated hoti jaayegi wese - wese count me ye updated value jaati jaayegi count ki current value bankar.
  };
  return (
    <>
      <h1>{count}</h1>
      <button onClick={IncCum}>Click Me</button>
    </>
  );
};

export default App;

// Hamne onclick ko onClick lika hai kyuki React me ham camal notation ka use karte hai, and hamne onClick={InCum} ke ander inCum me parasysis () nahi diye hai jabki ham jabki ham JavaScript me paralysis dete hai lekin React me nahi.

// Ab button par click karne par count ki value a jaayegi jo har click par increment +5 hoti jaayegi jo hamne setCount(count + 5) ke ander di hai.

// Yahi hai state management, ham yaha ek variable ka state (uski value) change kar paa rahe hai useState() ki help se and isi useState() ko ham Hooks kehte hai.
