import React, { useState } from 'react';

// React Hooks Challenge: Get Time on Refreshing and Clicking Button using useState Hook in Hindi

const App = () => {
  const [count, setCount] = useState(0);

  const times = new Date().toLocaleTimeString();

  const IncCum = () => {
    setCount(count + 5);
  };
  return (
    <>
      <h1>{times}</h1>
      <button onClick={IncCum}>Click Me</button>
    </>
  );
};

export default App;
