import React, { useState } from 'react';

const UseStateArray = () => {
  const bioData = [
    {
      id: 0,
      myName: 'shyam',
      age: 28,
    },
    {
      id: 0,
      myName: 'rajput',
      age: 29,
    },
  ];

  const [myArray, setmyArray] = useState(bioData);

  const clearArray = () => {
    setmyArray([]);
  };
  return (
    <>
      {myArray.map((curElm) => (
        <h1 className="h1style" key={curElm.id}>
          Name: {curElm.myName} & Age: {curElm.age}
        </h1>
      ))}

      <button onClick={clearArray} className="btn">
        Clear
      </button>
    </>
  );
};

export default UseStateArray;
