import React, { useState } from 'react';

const Apps = () => {
  const [item, setItem] = useState();

  const inputVal = (e) => {
    setItem(e.target.value);
  };

  return (
    <>
      <div className="outer-div">
        <div className="inner-div">
          <h1>ToDoList</h1>
          <input
            type="text"
            placeholder="Add a Items"
            onChange={inputVal}
            value={item}
          />
          <button onClick={addItem}>Add</button>
        </div>
      </div>
    </>
  );
};

export default Apps;
